mapper.select:
select

from pip_proxy_ip
where ip = #{ip,jdbcType=VARCHAR}
and port = #{port,jdbcType=INTEGER}

mapper.delete:
delete from pip_proxy_ip
where ip = #{ip,jdbcType=VARCHAR}
  and port = #{port,jdbcType=INTEGER}

mapper.insert:
insert into pip_proxy_ip (ip, port, type, 
  available, lag, fetch_time, 
  verify_time, invalid_times, discarded
  )
values (#{ip,jdbcType=VARCHAR}, #{port,jdbcType=INTEGER}, #{type,jdbcType=VARCHAR}, 
  #{available,jdbcType=BIT}, #{lag,jdbcType=REAL}, #{fetchTime,jdbcType=TIMESTAMP}, 
  #{verifyTime,jdbcType=TIMESTAMP}, #{invalidTimes,jdbcType=BIT}, #{discarded,jdbcType=BIT}
  )

mapper.update:
update pip_proxy_ip
set type = #{type,jdbcType=VARCHAR},
  available = #{available,jdbcType=BIT},
  lag = #{lag,jdbcType=REAL},
  fetch_time = #{fetchTime,jdbcType=TIMESTAMP},
  verify_time = #{verifyTime,jdbcType=TIMESTAMP},
  invalid_times = #{invalidTimes,jdbcType=BIT},
  discarded = #{discarded,jdbcType=BIT}
where ip = #{ip,jdbcType=VARCHAR}
  and port = #{port,jdbcType=INTEGER}
